# Create a raw transaction and add this message in it: "btrust builder 2025"

# Amount of 20,000,000 satoshis to this address: 2MvLcssW49n9atmksjwg2ZCMsEMsoj3pzUP 
# Use the UTXOs from the transaction below
# transaction=""
addy=2MvLcssW49n9atmksjwg2ZCMsEMsoj3pzUP
amount=0.2
tx=$(bitcoin-cli decoderawtransaction )
utxo_txid=$(echo $tx | jq -r '.txid')
# trimlen=3
# string=$(echo "btrust builder 2025" | sha256sum)
data=$(echo -n "btrust builder 2025" | xxd -p)
# data=627472757374206275696c6465722032303235
# data="$(echo ${string:0:${#string}-$trimlen})"
rawtxhex=$(bitcoin-cli -named createrawtransaction inputs='''[ { "txid": "'$utxo_txid'", "vout": 0 }, { "txid": "'$utxo_txid'", "vout": 1 } ]''' outputs='''{ "data": "'$data'", "'$addy'": 0.2}''')
echo $rawtxhex
